import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  Typography,
  Paper,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  CardActions,
  CircularProgress,
  Alert,
  IconButton,
  InputAdornment,
} from '@mui/material';
import {
  Search as SearchIcon,
  Upload as UploadIcon,
  Download as DownloadIcon,
  Preview as PreviewIcon,
} from '@mui/icons-material';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [file, setFile] = useState(null);
  const [searchParams, setSearchParams] = useState({
    name: '',
    title: '',
    company: '',
    ticket_type: '',
  });
  const [attendees, setAttendees] = useState([]);
  const [badges, setBadges] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchBadges();
  }, []);

  const fetchBadges = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/list-badges`);
      setBadges(response.data.badges);
    } catch (err) {
      setError('Failed to fetch badges');
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setFile(file);
    setLoading(true);
    setError(null);
    setSuccess(null);

    const formData = new FormData();
    formData.append('file', file);

    try {
      await axios.post(`${API_BASE_URL}/upload-csv`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setSuccess('Badges generated successfully!');
      fetchBadges();
    } catch (err) {
      setError(err.response?.data?.detail || 'Failed to upload file');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(`${API_BASE_URL}/search-attendees`, {
        params: searchParams,
      });
      setAttendees(response.data);
    } catch (err) {
      setError('Failed to search attendees');
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadBadge = async (filename) => {
    window.open(`${API_BASE_URL}/badge/${filename}`, '_blank');
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        Conference Badge Generator
      </Typography>

      <Paper sx={{ p: 3, mb: 4 }}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Button
              variant="contained"
              component="label"
              startIcon={<UploadIcon />}
              fullWidth
            >
              Upload CSV File
              <input
                type="file"
                hidden
                accept=".csv"
                onChange={handleFileUpload}
              />
            </Button>
            {file && (
              <Typography variant="body2" sx={{ mt: 1 }}>
                Selected file: {file.name}
              </Typography>
            )}
          </Grid>

          <Grid item xs={12} md={3}>
            <TextField
              fullWidth
              label="Name"
              value={searchParams.name}
              onChange={(e) =>
                setSearchParams({ ...searchParams, name: e.target.value })
              }
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField
              fullWidth
              label="Title"
              value={searchParams.title}
              onChange={(e) =>
                setSearchParams({ ...searchParams, title: e.target.value })
              }
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField
              fullWidth
              label="Company"
              value={searchParams.company}
              onChange={(e) =>
                setSearchParams({ ...searchParams, company: e.target.value })
              }
            />
          </Grid>
          <Grid item xs={12} md={3}>
            <TextField
              fullWidth
              label="Ticket Type"
              value={searchParams.ticket_type}
              onChange={(e) =>
                setSearchParams({ ...searchParams, ticket_type: e.target.value })
              }
            />
          </Grid>
          <Grid item xs={12}>
            <Button
              variant="contained"
              startIcon={<SearchIcon />}
              onClick={handleSearch}
              fullWidth
            >
              Search
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }}>
          {success}
        </Alert>
      )}

      {loading && (
        <Box display="flex" justifyContent="center" my={4}>
          <CircularProgress />
        </Box>
      )}

      <Grid container spacing={3}>
        {attendees.map((attendee, index) => (
          <Grid item xs={12} md={6} lg={4} key={index}>
            <Card>
              <CardContent>
                <Typography variant="h6">
                  {attendee['First Name']} {attendee['Last Name']}
                </Typography>
                <Typography color="textSecondary">
                  {attendee['Job Title']}
                </Typography>
                <Typography variant="body2">
                  {attendee['Company']}
                </Typography>
                <Typography variant="body2">
                  {attendee['Email']}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  size="small"
                  startIcon={<PreviewIcon />}
                  onClick={() =>
                    handleDownloadBadge(
                      `${attendee['Last Name']}_${attendee['First Name']}_${attendee['Order #']}.pdf`
                    )
                  }
                >
                  Preview Badge
                </Button>
                <Button
                  size="small"
                  startIcon={<DownloadIcon />}
                  onClick={() =>
                    handleDownloadBadge(
                      `${attendee['Last Name']}_${attendee['First Name']}_${attendee['Order #']}.pdf`
                    )
                  }
                >
                  Download
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default App;
